$f_contador = "./visitas.txt"; // The file where the hits are saved.
if( file_exists($f_contador) )
{
  $fp = fopen($f_contador, "r+");
  flock($fp, 1);
  $count = fgets($fp, 4096);
  $count++;
  fseek($fp,0);
  fputs($fp, $count);
  flock($fp, 3);
  fclose($fp);
}
// Copy the following text and paste it where you want the message appear.
echo $count." hits";

