$f_online = "./online.txt"; // The file where the data is saved.
if( file_exists($f_online) )
{
  $logoutTime = 300;
  $IP = $REMOTE_ADDR;
  $users = 0;
  $res = "";
  $fp = fopen($f_online,"r");
  $t = time();
  $content = fread($fp,filesize($f_online));
  fclose($fp);
  $lineas = split("\n",$content);
  for( $i = 0; $i < count($lineas); $i++ )
  {
    $datos = split(":",$lineas[$i]);
    $n = $t - $logoutTime;
    if ($datos[0] != $IP && $datos[1] > ($n))
    {
      $res .= "$datos[0]:$datos[1]\n";
      $usuarios++;
    }
  }
  $res .= "$IP:$t\n";
  $users++;
  $fp = fopen($f_online,"w");
  fwrite ($fp,$res);
  fclose($fp);
}
// Copy the following text and paste it where you want the message appear.
echo $users." people online";

