  PRIMARY KEY (ip)
);
-- SQL commands to install the script --
*/
$ip = $REMOTE_ADDR;
$past = time()-300;
mysql_query("DELETE FROM online WHERE time < $past");
$result = mysql_query("SELECT time FROM online WHERE ip='$ip'");
